private void jList1ValueChanged(javax.swing.event.ListSelectionEvent evt) {//GEN-FIRST:event_jList1ValueChanged
// TODO add your handling code here:
    if(jList1.getSelectedIndex() == 0)
    {
        jLabel3.setIcon(gyros) ;
    }
    else if(jList1.getSelectedIndex() == 1)
    {    
        jLabel3.setIcon(soutzoukaki) ;    
    }
    else if(jList1.getSelectedIndex() == 2)
    {
        jLabel3.setIcon(souvlaki) ;    
    }
    else if(jList1.getSelectedIndex() == 3)
    {
        jLabel3.setIcon(xoriatiki) ;    
    }
    else
    {
        jLabel3.setIcon(gemista) ;
    }
}//GEN-LAST:event_jList1ValueChanged

