    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int posotitaPiatwn = (Integer) jSpinner1.getValue() ;
        int posotitaPoton = (Integer) jSpinner2.getValue() ;
        if(jComboBox1.getSelectedIndex() == 0)
        {
            totalPrice += posotitaPoton * 2 ;
        }
        else if(jComboBox1.getSelectedIndex() == 1)
        {
            totalPrice += posotitaPoton * 3 ;
        }
        else if(jComboBox1.getSelectedIndex() == 2)
        {
            totalPrice += posotitaPoton * 1 ;
        }
        else 
        {
            totalPrice += posotitaPoton * 2 ;
        }
        if(jList1.getSelectedIndex() == 0)
        {
            totalPrice += posotitaPiatwn * 6 ; 
        }
        else if(jList1.getSelectedIndex() == 1)
        {
            totalPrice += posotitaPiatwn * 5 ; 
        }
        else if(jList1.getSelectedIndex() == 2)
        {
            totalPrice += posotitaPiatwn * 3 ; 
        }
        else if(jList1.getSelectedIndex() == 3)
        {
            totalPrice += posotitaPiatwn * 3 ;
        }
        else
        {
            totalPrice += posotitaPiatwn * 2 ;
        }
        jTextField1.setText("ΣΥΝΟΛΙΚΗ ΤΙΜΗ : " + totalPrice + " ΕΥΡΩ") ;
    }//GEN-LAST:event_jButton1ActionPerformed

